import math


def crack(text):
    deciphered = ''
    length = int(round(math.sqrt(len(text))))
    for j in range(length):
        for i in range(length):
            deciphered += text[length*i:][j]
    return deciphered


if __name__ == "__main__":
    text1 = ""
    text2 = 'aohpdnxnrilitxdsnefxxnogtfxxomceexxweolrxxftmyexx'
    print(crack(text1))
    print(crack(text2))
